// src/routes/places-routes.js
const express = require('express');
const { check } = require('express-validator');
const placesControllers = require('../controllers/places-controllers');

const router = express.Router();

// Route to get a place by ID
router.get('/:pid', placesControllers.getPlaceById);

// Route to get all places for a specific user
router.get('/user/:uid', placesControllers.getPlacesByUserId);

// Route to create a new place
router.post(
  '/',
  [
    check('title').not().isEmpty().withMessage('Title is required'),
    check('description').isLength({ min: 5 }).withMessage('Description must be at least 5 characters long'),
    check('address').not().isEmpty().withMessage('Address is required')
  ],
  placesControllers.createPlace
);

// Route to update a place by ID
router.patch(
  '/:pid',
  [
    check('title').not().isEmpty().withMessage('Title is required'),
    check('description').isLength({ min: 5 }).withMessage('Description must be at least 5 characters long')
  ],
  placesControllers.updatePlace
);

// Route to delete a place by ID
router.delete('/:pid', placesControllers.deletePlace);

module.exports = router;
